<?php\n// Start session at the beginning of your script\nsession_start();\n\n// Assume this is when a user logs in successfully\n$userId = 123; // This should be dynamically set based on the authenticated user\n$_SESSION['user_id'] = $userId;\n\n// To access the user ID later in different scripts\nif (isset($_SESSION['user_id'])) {\n    $currentUserId = $_SESSION['user_id'];\n    // Use $currentUserId to query specific user data from your tables\n} else {\n    // Handle case where user is not logged in or session has expired\n}\n\n// To clear the session when logging out\nsession_unset();\nsession_destroy();\n?>