<?php\n\n$socket = socket_create(AF_INET, SOCK_STREAM, SOL_TCP);\nif ($socket === false) {\n    echo "Could not create socket: " . socket_strerror(socket_last_error());\n} else {\n    $result = socket_connect($socket, '127.0.0.1', 10000);\n    if ($result === false) {\n        echo "Could not connect to server: " . socket_strerror(socket_last_error($socket));\n    } else {\n        $in = "Testing 1-2-3.\n";\n        socket_write($socket, $in, strlen($in));\n\n        $out = '';\n        while ($buf = socket_read($socket, 2048)) {\n            $out .= $buf;\n        }\n        echo "Received: " . $out;\n\n        socket_close($socket);\n    }\n}\n?>