// Controller\nclass LocationController {\n    public function getReportData($locationId) {\n        $location = $this->locationModel->getLocationById($locationId);\n        \n        // Transform NULL 'lastVisitDate' to 'Never'\n        if (is_null($location['lastVisitDate'])) {\n            $location['lastVisitText'] = 'Never';\n            $location['lastVisitColor'] = 'red';\n        } else {\n            $location['lastVisitText'] = $location['lastVisitDate'];\n            $location['lastVisitColor'] = 'black'; // or any default color\n        }\n        \n        return $location;\n    }\n}\n\n// View (pseudo-code)\necho "<p style='color: {$locationData['lastVisitColor']}'>{$locationData['lastVisitText']}</p>";