class QueryFailedException extends Exception {\n    // You can define common codes as class constants for better readability and maintainability.\n    const GENERIC_ERROR = 123;\n    const TIMEOUT_ERROR = 456;\n    // ... other specific errors\n\n    public function __construct($message, $code = self::GENERIC_ERROR, Throwable $previous = null) {\n        parent::__construct($message, $code, $previous);\n    }\n}\n\n// Usage with a generic error code\nif (!$this->connection[0]->query($this->query)) {\n    throw new QueryFailedException($this->connection[0]->error);\n}\n\n// Usage with a specific timeout error code (assuming there's a reason to differentiate)\nif ($this->connectionTimedOut) {\n    throw new QueryFailedException($this->connection[0]->error, QueryFailedException::TIMEOUT_ERROR);\n}