<?php\ninclude("run_connect.php"); // this connects to the database, this works\n\nif(isset($_POST['first'])) {\n    $firstName = $_POST['first'];\n\n    // Using prepared statements for better security and avoiding SQL injection\n    if($stmt = mysqli_prepare($conn, "INSERT INTO tblHurray(first) VALUES (?)")) {\n        mysqli_stmt_bind_param($stmt, "s", $firstName);\n        mysqli_stmt_execute($stmt);\n\n        echo "<b><center>Database Output</center></b><br><br>";\n\n        $result = mysqli_query($conn, "SELECT * FROM tblHurray");\n        while ($row = mysqli_fetch_assoc($result)) {\n            echo $row['ID'] . ": " . htmlspecialchars($row['first']) . "<br />";\n        }\n    } else {\n        // Handle error\n        echo "Error in preparing statement: " . mysqli_error($conn);\n    }\n\n    // Close the statement\n    mysqli_stmt_close($stmt);\n\n} else {\n    echo "Form data not set.";\n}\n\n// Close the connection\nmysqli_close($conn);\n?>