// Your data array to be sent as JSON\n$data = array(\n    'status' => 'success',\n    'message' => 'File uploaded successfully'\n);\n\n// Get the response object\n$response = $this->getResponse();\n\n// Set the header to text/html\n$response->setHeader('Content-Type', 'text/html');\n\n// Send JSON data as string in the body\n$response->appendBody(json_encode($data));\n\n// Return a false value to prevent additional output from being sent\nreturn false;