<?php\n\n$xmlString = <<<XML\n<favorites>\n    <interesting>\n        <type>Movie</type>\n        <character>James Bond</character>\n        <name>Casino Royale</name>\n    </interesting>\n    <interesting>\n        <type>Movie</type>\n        <character>Jason Bourne</character>\n        <name>Bourne Identity</name>\n    </interesting>\n    <interesting>\n        <type>Book</type>\n        <character>Lindsay Ford</character>\n        <name>Shantaram</name>\n    </interesting>\n</favorites>\nXML;\n\n// Load the XML content\n$xml = simplexml_load_string($xmlString);\n\n// Initialize an array to hold grouped data\n$groupedData = [];\n\n// Loop through each 'interesting' element and group by type\nforeach ($xml->interesting as $item) {\n    $type = (string)$item->type;\n    if (!isset($groupedData[$type])) {\n        $groupedData[$type] = [];\n    }\n    $groupedData[$type][] = (string)$item->name . ' - ' . (string)$item->character;\n}\n\n// Output the grouped data\nforeach ($groupedData as $type => $items) {\n    echo "<strong>{$type}</strong>\n<ul>";\n    foreach ($items as $item) {\n        echo "<li>{$item}</li>";\n    }\n    echo "</ul>\n";\n}\n?>