<?php\n\nfunction convertSoapDateString($soapDate) {\n    $datetime = DateTime::createFromFormat('Y-m-d\TH:i:s\Z', $soapDate, new DateTimeZone('UTC'));\n    if ($datetime) {\n        // Convert to desired timezone if necessary\n        $datetime->setTimezone(new DateTimeZone('America/New_York')); // Change this as needed\n        return $datetime;\n    } else {\n        throw new Exception("Invalid date format");\n    }\n}\n\n// Example usage\n$soapDateString = "2009-06-04T13:23:25Z"; // This is just an example, should come from soap response\ntry {\n    $convertedDate = convertSoapDateString($soapDateString);\n    echo $convertedDate->format('Y-m-d H:i:s');\n} catch (Exception $e) {\n    echo "Error: " . $e->getMessage();\n}