<?php\nfunction getMaxFilenameLength($path) {\n    $maxLength = 0;\n    for ($i = 1; $i <= 255; $i++) { // Start testing up to 255 bytes, common max length for ext4\n        $testFilename = str_repeat('a', $i);\n        $filePath = "$path/$testFilename";\n        if (touch($filePath)) {\n            $maxLength = $i;\n            unlink($filePath); // Clean up after test\n        } else {\n            break; // Stop when touch fails\n        }\n    }\n    return $maxLength;\n}\n\n$testPath = "/tmp"; // Use a writable directory for testing\n$maxFilenameLength = getMaxFilenameLength($testPath);\necho "Maximum filename length in $testPath: $maxFilenameLength bytes\n";\n?>