class PageBuilder {\n    public function writeHeader($customText = '') {\n        echo '<div class="header">';\n        include('left.php');\n        echo "<h1>{$this->escapeHtml($customText)}</h1>";\n        echo '</div>';\n    }\n\n    private function escapeHtml($string) {\n        return htmlspecialchars($string, ENT_QUOTES, 'UTF-8');\n    }\n}\n\n// Usage\n$pageBuilder = new PageBuilder();\n$pageBuilder->writeHeader('Welcome to My Site');