// 1) Reading from a separate include file (variables.php)\ninclude 'variables.php'; // Assume $var1, $var2... are defined here\n\n// 2) Reading from a text file using fopen\n$file_path = 'strings.txt';\n$fp = fopen($file_path, 'r');\nif ($fp) {\n    while (($line = fgets($fp)) !== false) {\n        list($key, $value) = explode('=', trim($line));\n        ${$key} = trim($value, " \"'\n"); // Assign variable dynamically\n    }\n    fclose($fp);\n}\n\n// 3) Reading from a (small) database - simple MySQL query\n$mysqli = new mysqli("localhost", "user", "password", "database");\nif ($result = $mysqli->query("SELECT key, value FROM strings LIMIT 20")) {\n    while ($row = $result->fetch_assoc()) {\n        ${$row['key']} = $row['value'];\n    }\n    $result->free();\n}\n$mysqli->close();