// Include the Composer autoloader\nrequire_once __DIR__ . '/path/to/your/symfony/app/vendor/autoload.php';\n\nuse Symfony\Component\HttpFoundation\Request;\n\n// Kernel creation depends on your environment (dev or prod) and debug mode\n$kernel = new \App\Kernel('prod', false);\n$request = Request::createFromGlobals();\n$response = $kernel->handle($request);\n\n// Now you can get the Entity Manager from the service container\n$container = $kernel->getContainer();\n$entityManager = $container->get('doctrine.orm.entity_manager');\n\n// Use the entity manager to access and manipulate your models/entities\n$repository = $entityManager->getRepository('App\Entity\YourEntity');\n$entity = $repository->findOneBySomeField($someValue);\n\n// Always ensure you follow best practices regarding error handling and resource management\n\n// When your script is done, terminate the kernel\n$kernel->terminate($request, $response);