$sourceUrl = 'http://example.com/file.zip';\n$destinationPath = '/local/path/to/save/file.zip';\n\n// Define an options array\n$options = [\n    'http' => [\n        'method' => 'GET',\n        'header' => "User-Agent: PHP\r\n"\n                    . "Accept: */*\r\n"\n    ]\n];\n\n// Create a stream context resource\n$context = stream_context_create($options);\n\n// Perform the file copy using the custom context\nif(copy($sourceUrl, $destinationPath, $context)) {\n    echo 'File copied successfully.';\n} else {\n    echo 'Failed copying file.';\n}