// application/helpers/my_string_helper.php\nif ( ! function_exists('custom_slugify'))\n{\n    function custom_slugify($string)\n    {\n        // Your custom slugification logic here\n        return strtolower(trim(preg_replace('/[\s-]+/', '-', preg_replace('/[^A-Za-z0-9-]+/', '', $string)), '-'));\n    }\n}\n\nif ( ! function_exists('another_custom_function'))\n{\n    function another_custom_function($param)\n    {\n        // Implement your custom logic here\n        return strrev($param); // Example: reverse string\n    }\n}