require_once 'vendor/autoload.php';\n\nsession_start();\n\n$client = new Google_Client();\n$client->setApplicationName('Google Calendar Event Creator');\n$client->setScopes(Google_Service_Calendar::CALENDAR);\n$client->setAuthConfig('path/to/your/client_secret.json');\n$client->setAccessType('offline');\n\n// Load previously authorized credentials from a file.\n$credentialsPath = 'token.json';\nif (file_exists($credentialsPath)) {\n    $accessToken = json_decode(file_get_contents($credentialsPath), true);\n}\n$client->setAccessToken($accessToken);\n\n// If there is no previous token or it's expired, get a new one.\nif ($client->isAccessTokenExpired()) {\n    if ($client->getRefreshToken()) {\n        $client->fetchAccessTokenWithRefreshToken($client->getRefreshToken());\n    } else {\n        // Redirect the user to Google's OAuth 2.0 server for authentication.\n        header('Location: ' . filter_var($client->createAuthUrl(), FILTER_SANITIZE_URL));\n        exit;\n    }\n}\n\n// Save the token to a file.\nif (!file_exists(dirname($credentialsPath))) {\n    mkdir(dirname($credentialsPath), 0700, true);\n}\nfile_put_contents($credentialsPath, json_encode($client->getAccessToken()));\n\n$service = new Google_Service_Calendar($client);\n\n// Replace with your calendar ID\n$calendarId = 'your_calendar_id@group.calendar.google.com';\n\n$event = new Google_Service_Calendar_Event([\n    'summary' => 'Event Title',\n    'location' => 'Event Location',\n    'description' => 'Event Description',\n    'start' => [\n        'dateTime' => '2023-10-01T09:00:00-07:00',\n        'timeZone' => 'America/Los_Angeles',\n    ],\n    'end' => [\n        'dateTime' => '2023-10-01T17:00:00-07:00',\n        'timeZone' => 'America/Los_Angeles',\n    ],\n]);\n\n$event = $service->events->insert($calendarId, $event);\n\necho "Event created: {$event->htmlLink}";