<?php\nsession_start();\n\nfunction pushStack($id) {\n    if (!isset($_SESSION['stack'])) {\n        $_SESSION['stack'] = [];\n    }\n    array_push($_SESSION['stack'], $id);\n}\n\nfunction clearStack() {\n    if (isset($_SESSION['stack'])) {\n        unset($_SESSION['stack']);\n    }\n}\n\n// Example usage:\nif(isset($_GET['action']) && $_GET['action'] == 'push') {\n    pushStack($_GET['id']);\n}\nif(isset($_GET['action']) && $_GET['action'] == 'clear') {\n    clearStack();\n}\n\nheader('Content-Type: application/json');\necho json_encode([\n    'stack' => isset($_SESSION['stack']) ? array_reverse($_SESSION['stack']) : []\n]);\n?>