<?php\n// Sample CXML string\n$cxmlString = <<<XML\n<Request deploymentMode="production">\n    <Header>\n        <From>\n            <Credential domain="duns">\n                <Identity>identity_value</Identity>\n                <SharedSecret>secret</SharedSecret>\n            </Credential>\n        </From>\n        <!-- More tags can go here -->\n    </Header>\n    <Message>\n        <PunchOutSetupRequest PunchOutOrderMessageURL="http://example.com/punchout">\n            <BuyerCookie>UniqueBuyerCookieValue</BuyerCookie>\n            <!-- More tags can go here -->\n        </PunchOutSetupRequest>\n    </Message>\n</Request>\nXML;\n\n// Convert the CXML string into a SimpleXMLElement object\n$xml = simplexml_load_string($cxmlString);\n\n// Accessing tag values\n$requestType = $xml->getName(); // Returns 'Request'\n$header = $xml->Header;\n$credential = $header->From->Credential;\n\n// Accessing attribute values\n$deploymentMode = (string) $xml['deploymentMode']; // Returns the value of deploymentMode attribute\n$dunsDomain = (string) $credential['domain']; // Returns the value of domain attribute\n\n// Output some values to see if we've parsed it correctly\necho "Request Type: " . $requestType . PHP_EOL;\necho "Deployment Mode: " . $deploymentMode . PHP_EOL;\necho "Credential Domain: " . $dunsDomain . PHP_EOL;\n?>