<?php\nfunction getPaginatedAnswers($questionId, $page, $itemsPerPage = 20) {\n    $connection = new PDO('mysql:host=localhost;dbname=your_db', 'user', 'password');\n    \n    // Calculate the offset\n    $offset = ($page - 1) * $itemsPerPage;\n    \n    // Query to get paginated answers\n    $sql = "SELECT answer FROM answers WHERE question_id = :questionId LIMIT :limit OFFSET :offset";\n    $stmt = $connection->prepare($sql);\n    $stmt->bindParam(':questionId', $questionId, PDO::PARAM_INT);\n    $stmt->bindParam(':limit', $itemsPerPage, PDO::PARAM_INT);\n    $stmt->bindParam(':offset', $offset, PDO::PARAM_INT);\n    $stmt->execute();\n    \n    return $stmt->fetchAll(PDO::FETCH_ASSOC);\n}\n\nfunction getTotalAnswers($questionId) {\n    $connection = new PDO('mysql:host=localhost;dbname=your_db', 'user', 'password');\n    \n    // Query to get total number of answers\n    $sql = "SELECT COUNT(*) as total FROM answers WHERE question_id = :questionId";\n    $stmt = $connection->prepare($sql);\n    $stmt->bindParam(':questionId', $questionId, PDO::PARAM_INT);\n    $stmt->execute();\n    $result = $stmt->fetch(PDO::FETCH_ASSOC);\n    \n    return intval($result['total']);\n}\n\n// Example usage\n$page = 3; // Current page number\n$questionId = 1; // ID of the question\n$itemsPerPage = 20;\n\n$answers = getPaginatedAnswers($questionId, $page, $itemsPerPage);\n$totalAnswers = getTotalAnswers($questionId);\n\n$totalPages = ceil($totalAnswers / $itemsPerPage);\n\n// HTML to display answers and pagination links\necho "<h1>Answers:</h1>";\nforeach ($answers as $answer) {\n    echo "<p>{$answer['answer']}</p>";\n}\n\necho "<div class='pagination'>";\nfor ($i = 1; $i <= $totalPages; $i++) {\n    echo "<a href='?page={$i}'>{$i}</a> ";\n}\necho "</div>";\n?>