function getImageSrc($html) {\n    $dom = new DOMDocument();\n    \n    // Suppress warnings due to malformed HTML\n    libxml_use_internal_errors(true);\n    $dom->loadHTML($html);\n    libxml_clear_errors();\n\n    // Get the first <img> tag\n    $images = $dom->getElementsByTagName('img');\n    if ($images->length > 0) {\n        $image = $images->item(0);\n        return $image->getAttribute('src');\n    }\n\n    return null; // Return null if no img tag found\n}\n\n// Example usage:\n$htmlContent = '<html><body><img src="example.jpg" alt="Example Image"></body></html>';\necho getImageSrc($htmlContent); // Outputs: example.jpg