function emailUser($table, $subject, $message) {\n    // Connect to the database\n    $mysqli = new mysqli("localhost", "username", "password", "database");\n\n    if ($mysqli->connect_errno) {\n        echo "Failed to connect to MySQL: (" . $mysqli->connect_errno . ") " . $mysqli->connect_error;\n        return;\n    }\n\n    $query = "SELECT * FROM $table";\n    $result = $mysqli->query($query);\n\n    if (!$result) {\n        die('Query failed: ' . $mysqli->error);\n    }\n\n    while ($row = $result->fetch_assoc()) {\n        $to = $row['email'];\n        $to_all .= '<li>' . htmlspecialchars($to) . '</li>';\n\n        // Set up the HTML MIME Mail message\n        require_once 'htmlMimeMail.php'; // Ensure this path is correct and accessible\n\n        $mail = new htmlMimeMail();\n        $mail->setHTML($message);\n        $mail->setSubject($subject);\n        $mail->setSMTPParams('mail.site.net', 25, 'site.net');\n        $mail->setReturnPath("email@site.net");\n        $mail->setFrom("email@site.net");\n\n        // Send the email\n        try {\n            $mail_result = $mail->send(array($to), 'smtp');\n\n            if (!$mail_result) {\n                throw new Exception(print_r($mail->errors, true));\n            }\n        } catch (Exception $e) {\n            echo '<p>Error sending mail to ' . htmlspecialchars($to) . ': ' . $e->getMessage() . '</p>';\n            continue;\n        }\n    }\n\n    print '<h3>Mail successfully sent to:</h3>';\n    print '<ul>' . $to_all . '</ul>';\n\n    // Close the database connection\n    $result->close();\n    $mysqli->close();\n}