$file = $target_path;\n$data = file($file) or die('Could not read file!');\n\n$sections = [];\n$currentSection = [];\n\nforeach ($data as $line) {\n    if (trim($line) === '') {\n        // If we encounter a blank line, save the current section and start a new one\n        if (!empty($currentSection)) {\n            $sections[] = array_map('trim', $currentSection); // Trim whitespace from each element for clarity\n            $currentSection = [];\n        }\n    } else {\n        // Add non-empty lines to the current section\n        $currentSection[] = $line;\n    }\n}\n\n// Don't forget to add the last section if there's no trailing blank line\nif (!empty($currentSection)) {\n    $sections[] = array_map('trim', $currentSection);\n}\n\nprint_r($sections); // For debugging purposes, to see how your data is grouped