$url = 'ftp://username:password@clientserver.com/path/to/destination/file.txt';\n$localFilePath = '/path/to/local/file.txt';\n\n// Initialize cURL session\n$ch = curl_init();\n\n// Set the URL for the request\ncurl_setopt($ch, CURLOPT_URL, $url);\n\n// Enable uploading of a file\ncurl_setopt($ch, CURLOPT_UPLOAD, true);\n\n// Open the local file to upload\n$fileHandle = fopen($localFilePath, 'r');\nif ($fileHandle) {\n    curl_setopt($ch, CURLOPT_INFILE, $fileHandle);\n}\n\n// Execute the cURL request\n$result = curl_exec($ch);\n\n// Check for errors and close the resources\nif (!$result && !curl_errno($ch)) {\n    echo "File uploaded successfully";\n} else {\n    echo 'cURL Error: ' . curl_error($ch);\n}\nfclose($fileHandle);\ncurl_close($ch);