<?php\nclass YourCompany_Feedback_IndexController extends Mage_Core_Controller_Front_Action {\n    public function indexAction() {\n        $this->loadLayout();\n        $this->_initLayoutMessages('customer/session');\n        $this->_initLayoutMessages('catalog/session');\n        $this->renderLayout();\n    }\n\n    public function postAction() {\n        if ($data = $this->getRequest()->getPost()) {\n            try {\n                $postObject = new Varien_Object();\n                $postObject->setData($data);\n\n                // Validate data\n                if (!$postObject->validateData(array('name'))) {\n                    throw new Exception(Mage::helper('feedback')->__('Invalid form data.'));\n                }\n\n                // Process the data (e.g., save to database or send email)\n                Mage::getSingleton('core/session')->addSuccess(Mage::helper('feedback')->__('Thanks for your feedback.'));\n            } catch (Exception $e) {\n                Mage::getSingleton('core/session')->addError($e->getMessage());\n                $postObject = new Varien_Object();\n                $postObject->setData($data);\n                Mage::getSingleton('customer/session')->setFeedbackFormData($postObject);\n            }\n        }\n\n        $this->_redirect('*/*/');\n    }\n}