<?php\n\n$myemail = "****@myemail.com"; \n\n// Setting default timezone to Asia/Singapore (GMT+8)\ndate_default_timezone_set('Asia/Singapore');\n\n$todayis = date("l, F j, Y, g:i a");\n\nif(empty($_POST['visitormail']) || !filter_var($_POST['visitormail'], FILTER_VALIDATE_EMAIL)) {\n    echo "<h2>Use Back - Enter valid e-mail</h2>\n";\n    $badinput = "<h2>Feedback was NOT submitted</h2>\n";\n}\n\nif(empty($_POST['visitor']) || empty($_POST['visitormail']) || empty($_POST['notes'])) {\n    echo "<h2>Use Back - fill in all fields</h2>\n";\n}\necho $badinput ?? '';\n\n$visitor = htmlspecialchars($_POST['visitor']);\n$visitormail = htmlspecialchars($_POST['visitormail']);\n$weburl = htmlspecialchars($_POST['weburl']);\n$notes = nl2br(htmlspecialchars($_POST['notes']));\n$ip = $_SERVER['REMOTE_ADDR'];\n$httpagent = $_SERVER['HTTP_USER_AGENT'];\n$httpref = $_SERVER['HTTP_REFERER'];\n\n$message = " $todayis \n\nFrom: $visitor ($visitormail)\n\nWebsite URL: $weburl \n\nMessage: $notes \n \nAdditional Info : IP = $ip \n\nBrowser Info: $httpagent \n\nReferral : $httpref \n\n";\n$from = "From: $myemail\r\n";\n\n// Send email to the recipient\nif ($myemail != "") {\n    mail($myemail, "Feedback Received from $visitor", $message, $from);\n}\n\n// Send thank you email to the visitor\n$thankYouMessage = "Thank you for your feedback, $visitor. Here is the message you wrote: \n\n$notes";\n$headers = "From: $myemail\r\n"; // Ensure the From header is set properly\nmail($visitormail, "Thank You for Your Feedback", $thankYouMessage, $headers);\n\n?>