function clearReferences(Node $node) {\n    $node->setParent(null);\n    foreach ($node->children as &$child) {\n        clearReferences($child);\n        $child = null; // Break reference to child node\n    }\n    $node->children = [];\n}\n\n// Example usage:\n$node0 = new Node;\n$node1 = new Node;\n\n$node0->addChild($node1);\n\n// Clear references when done with the tree structure if needed\nclearReferences($node0);