<?php\n$login = $_POST['login'];\n$password = $_POST['password'];\n\n// Use mysqli instead of mysql_connect and mysql_select_db\n$connection = new mysqli('localhost', 'root', 'abc123', 'aun_vox');\n\nif ($connection->connect_error) {\n    die("Connection failed: " . $connection->connect_error);\n}\n\n// Prepare and bind\n$stmt = $connection->prepare("SELECT password FROM customer WHERE login = ?");\n$stmt->bind_param("s", $login);\n\n// Execute query\n$stmt->execute();\n$stmt->store_result();\n\nif ($stmt->num_rows > 0) {\n    // Bind result variables\n    $stmt->bind_result($db_pass);\n    $stmt->fetch();\n\n    // Verify password\n    if (password_verify($password, $db_pass)) {\n        echo "You did it.";\n    } else {\n        echo "Wrong.";\n    }\n} else {\n    echo "No user found with that login.";\n}\n\n// Close statement and connection\n$stmt->close();\n$connection->close();\n?>