<?php\n// The given timestamp\n$timestamp = "Sat Oct 25 21:55:29 +0000 2008";\n\n// Create a DateTime object from the timestamp\n$date = DateTime::createFromFormat('D M j H:i:s O Y', $timestamp);\n\n// Get the current date and time\n$now = new DateTime();\n\n// Calculate the difference between the two dates\n$interval = $date->diff($now);\n\n// Format the interval into a string\nif ($interval->y > 0) {\n    echo "It has been {$interval->y} year(s)!";\n} elseif ($interval->m > 0) {\n    echo "It has been {$interval->m} month(s)!";\n} elseif ($interval->d > 0) {\n    echo "It has been {$interval->d} day(s)!";\n} elseif ($interval->h > 0) {\n    echo "It has been {$interval->h} hour(s)!";\n} elseif ($interval->i > 0) {\n    echo "It has been {$interval->i} minute(s)!";\n} else {\n    echo "It has been just a few seconds!";\n}\n?>