function calculateTotal($items) {\n    $total = 0;\n    foreach ($items as $item) {\n        $total += $item['price'] * $item['quantity'];\n    }\n    return $total;\n}\n\n$shoppingCartItems = [\n    ['name' => 'Item1', 'price' => 9.99, 'quantity' => 2],\n    ['name' => 'Item2', 'price' => 5.49, 'quantity' => 3]\n];\n\n$totalCost = calculateTotal($shoppingCartItems);\necho "Total Cost: $" . number_format($totalCost, 2);