class Base {\n    public function __construct() {\n        $childClassName = get_called_class();\n        echo "The child class is: " . $childClassName;\n    }\n}\n\nclass Child1 extends Base {}\n\nclass Child2 extends Base {}\n\n// Example usage:\n$child1Instance = new Child1(); // Outputs: The child class is: Child1\n$child2Instance = new Child2(); // Outputs: The child class is: Child2