function getUsersFilePath($userId) {\n    // Define a base directory where user data is stored\n    $baseDir = '/path/to/user/data/';\n\n    // Validate that userId is alphanumeric to prevent directory traversal\n    if (!preg_match('/^[a-zA-Z0-9]+$/', $userId)) {\n        throw new Exception('Invalid user ID');\n    }\n\n    // Create the full path to the user's data file\n    $filePath = $baseDir . basename($userId) . '.txt';\n\n    // Ensure the filePath is still within the base directory\n    if (strpos(realpath($filePath), realpath($baseDir)) !== 0) {\n        throw new Exception('Invalid user ID');\n    }\n\n    return $filePath;\n}\n\ntry {\n    $userId = $_GET['id']; // Assume id is passed as a GET parameter\n    $filePath = getUsersFilePath($userId);\n    if (file_exists($filePath)) {\n        echo 'File exists: ' . htmlspecialchars($filePath, ENT_QUOTES, 'UTF-8');\n    } else {\n        echo 'File does not exist.';\n    }\n} catch (Exception $e) {\n    echo 'Error: ' . htmlspecialchars($e->getMessage(), ENT_QUOTES, 'UTF-8');\n}