// Calculate the age from a given birth date\n// Example: GetAge("1986-06-18");\nfunction getAge($Birthdate) {\n    $birthDate = new DateTime($Birthdate);\n    $today     = new DateTime();\n    \n    // Calculate the difference between today and the birthdate\n    $ageDiff   = $today->diff($birthDate);\n    \n    // Get the years from the date difference\n    return $ageDiff->y;\n}\n\necho getAge('1990-04-04');