// Assuming the models are setup correctly: User, Tip, FriendsUser\n// Add 'Containable' to the $actsAs array in your AppModel or directly in this model if preferred\nclass Tip extends AppModel {\n    public $actsAs = array('Containable');\n    \n    // Define your associations properly in your User and Tip models\n}\n\n// In your controller or where you need to perform the find operation:\n$user_id = 2; // Replace with dynamic user_id as needed\n\n$tips = $this->Tip->find('all', array(\n    'conditions' => array(\n        'OR' => array(\n            'Tip.user_id' => $user_id,\n            'FriendsUser.user_id' => $user_id\n        )\n    ),\n    'joins' => array(\n        array(\n            'table' => 'users',\n            'alias' => 'User',\n            'type' => 'INNER',\n            'conditions' => array(\n                'Tip.user_id = User.id'\n            )\n        ),\n        array(\n            'table' => 'friends_users',\n            'alias' => 'FriendsUser',\n            'type' => 'LEFT',\n            'conditions' => array(\n                'Tip.user_id = FriendsUser.friend_id'\n            )\n        )\n    ),\n    'contain' => array('User'), // To contain user details if needed\n    'limit' => 30, // Limit the results\n    'recursive' => -1 // Prevent CakePHP from automatically joining associated models\n));\n\n// $tips now contains the desired data