<?php\n// Original array from MySQL query result\n$originalArray = [\n    (object)['bid' => 18, 'name' => 'Cafe Domingo', 'imageurl' => 'sp_domingo.gif', 'clickurl' => '#'],\n    (object)['bid' => 19, 'name' => 'Industrial Sweeping Services', 'imageurl' => 'sp_iss.gif', 'clickurl' => 'http://www.industrialsweeping.com.au/'],\n    // ... other elements\n    (object)['bid' => 23, 'name' => 'Redwood Gardens Chinese Restaurant', 'imageurl' => 'sp_redwood.gif', 'clickurl' => 'http://www.redwoodgardens.com.au/']\n];\n\n// Number of arrays to split into\n$numArrays = 4;\n\n// Calculate the size of each chunk\n$chunkSize = ceil(count($originalArray) / $numArrays);\n\n// Split the array into chunks\n$splitArrays = array_chunk($originalArray, $chunkSize);\n\n// Print the result\nforeach ($splitArrays as $index => $array) {\n    echo "Array " . ($index + 1) . ":\n";\n    print_r($array);\n}\n?>