<?php\n// Set up the arguments for WP_Query\n$args = array(\n    'post_type'      => 'page',\n    'posts_per_page' => -1, // -1 means no limit\n    'post_parent'    => get_the_ID(), // This will fetch all child pages of the current page\n);\n\n// Create a new query instance\n$query = new WP_Query($args);\n\n// The Loop\nif ($query->have_posts()) {\n    while ($query->have_posts()) {\n        $query->the_post();\n        // Use template tags within the loop\n        echo '<h2>' . get_the_title() . '</h2>';\n        echo '<a href="' . get_the_permalink() . '">Read more</a>';\n    }\n}\n\n// Reset post data to avoid conflicts\nwp_reset_postdata();\n?>