function get_param($param_name, $param_type = 0)\n{\n    $param_value = "";\n    if ($param_type != POST && isset($_POST[$param_name])) {\n        $param_value = $_POST[$param_name];\n    } elseif ($param_type != GET && isset($_GET[$param_name])) {\n        $param_value = $_GET[$param_name];\n    }\n    return strip($param_value);\n}\n\nfunction strip($value)\n{\n    // Assuming no magic quotes in modern PHP environment\n    // Perform additional sanitization as needed\n    return htmlspecialchars($value, ENT_QUOTES, 'UTF-8');\n}