<?php\nheader('Content-Type: application/json');\n$pdo = new PDO("mysql:host=your_host;dbname=your_db", "username", "password");\n\nif (!empty($_POST['b']) && !empty($_POST['p'])) {\n    $bizID = intval($_POST['b']);\n    $show = intval($_POST['p']);\n\n    if ($bizID > 0 && in_array($show, [0, 1])) {\n        $stmt = $pdo->prepare("UPDATE listings SET show_online = :show WHERE id = :id LIMIT 1");\n        $result = $stmt->execute([':show' => $show, ':id' => $bizID]);\n\n        if ($result) {\n            echo json_encode(["id" => $bizID, "return" => $show]);\n            exit;\n        }\n    }\n}\necho json_encode([]); // Return empty JSON on failure\n?>