<?php\n$dsn = 'mysql:host=localhost;dbname=your_database;charset=utf8';\n$username = ''; // your database username\n$password = ''; // your database password\n\ntry {\n    $pdo = new PDO($dsn, $username, $password);\n} catch (\PDOException $e) {\n    throw new \PDOException($e->getMessage(), (int)$e->getCode());\n}\n\n$pk = filter_input(INPUT_GET, 'pk', FILTER_SANITIZE_NUMBER_INT); // Sanitize input\nif (!$pk || !is_numeric($pk)) {\n    die("Invalid PK provided.");\n}\n\n$query = "SELECT ARTICLE_NAME, WATCH FROM AUCTIONS WHERE ARTICLE_NO = :article_no LIMIT 5";\n$stmt = $pdo->prepare($query);\n$stmt->execute([':article_no' => $pk]);\n\nwhile ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n    echo "<h1>" . htmlspecialchars($row['ARTICLE_NAME']) . " - Watch: " . htmlspecialchars($row['WATCH']) . "</h1>";\n}