<?php\n// Assuming $sections_query is the result of a mysqli query\n\n// Initialize a connection first\n$connection = new mysqli("host", "username", "password", "database");\n\n// Check connection\nif ($connection->connect_error) {\n    die("Connection failed: " . $connection->connect_error);\n}\n\n// Execute your query, replace with your actual query string\n$query = "SELECT * FROM sections";\n$result = $connection->query($query);\n\n// Loop through each row in the result set\nwhile ($row_sections = $result->fetch_assoc()) {\n    // Accessing the 'section_name' column from the associative array\n    echo "<h3>" . htmlspecialchars($row_sections['section_name']) . "</h3>";\n}\n\n// Close the connection\n$connection->close();\n?>