// settings.php\n<?php\nnamespace Core;\n\nclass Settings\n{\n    private $mysqli;\n    public function __construct($mysqli)\n    {\n        $this->mysqli = $mysqli;\n    }\n\n    public function get($what)\n    {\n        $stmt = $this->mysqli->prepare("SELECT value FROM " . $_SERVER["db_prefix"] . "settings WHERE key=?");\n        $stmt->bind_param('s', $what);\n        $stmt->execute();\n        $stmt->store_result();\n        if ($stmt->num_rows > 0) {\n            $stmt->bind_result($value);\n            $stmt->fetch();\n            return $value;\n        } else {\n            return -1;\n        }\n    }\n}\n?>