<?php\nuse PHPMailer\PHPMailer\PHPMailer;\nuse PHPMailer\PHPMailer\Exception;\n\nrequire 'vendor/autoload.php';\n\nfunction sendEmailWithInlineImage($toEmail, $subject, $body, $filePath) {\n    $mail = new PHPMailer(true);\n\n    try {\n        //Server settings\n        $mail->isSMTP();\n        $mail->Host       = 'smtp.example.com';  // SMTP server\n        $mail->SMTPAuth   = true;\n        $mail->Username   = 'user@example.com';\n        $mail->Password   = 'secret';\n        $mail->SMTPSecure = PHPMailer::ENCRYPTION_STARTTLS;\n        $mail->Port       = 587;\n\n        //Recipients\n        $mail->setFrom('from@example.com', 'Sender Name');\n        $mail->addAddress($toEmail);               // Add a recipient\n\n        // Content\n        $mail->isHTML(true);\n        $mail->Subject = $subject;\n        $mail->Body    = $body;\n        $mail->AltBody = 'This is the body in plain text for non-HTML mail clients';\n\n        $cid = md5(uniqid(time())); // Generate a unique ID\n        $mail->AddEmbeddedImage($filePath, $cid, basename($filePath));  // Embed an image as CID\n\n        $mail->send();\n    } catch (Exception $e) {\n        echo "Message could not be sent. Mailer Error: {$mail->ErrorInfo}";\n    }\n}\n\n// Example usage\n$toEmail = 'recipient@example.com';\n$subject = 'Test Email with Inline Image';\n$body = '<h1>Hello!</h1><p>This is a test email.</p><img src="cid:' . $cid . '" alt="Logo"/>';\nsendEmailWithInlineImage($toEmail, $subject, $body, '/path/to/logo.png');\n?>