function bcceil($number) {\n    $fraction = bcsub($number, bcfloor($number), 0);\n    return empty($fraction) ? $number : bcadd(bcfloor($number), '1', 0);\n}\n\nfunction bcfloor($number) {\n    return bcmul(bcdiv($number, '1', 0), '1', 0);\n}\n\nfunction bcrnd($number, $scale = 0) {\n    $floorValue = bcfloor($number);\n    $fraction = bcsub($number, $floorValue, 0 + 1);\n    \n    if (bccomp($fraction, '0.5') >= 0) {\n        return bcadd($floorValue, '1', 0);\n    } else {\n        return $floorValue;\n    }\n}\n\n// Usage\n$number = '31497230840470473074370324734723042.6';\n\necho "Ceil: " . bcceil($number) . "\n"; // Expected result: 31497230840470473074370324734723043\necho "Floor: " . bcfloor($number) . "\n"; // Expected result: 31497230840470473074370324734723042\necho "Round: " . bcrnd($number, 0) . "\n"; // Expected result: 31497230840470473074370324734723043