function extractDimensions($string) {\n    // Regular expression to match numbers possibly followed by a unit (e.g., mm)\n    preg_match_all('/\b\d+(\.\d+)?(?=\s*[\D]*$)/', $string, $matches);\n    return array_map('floatval', $matches[0]);\n}\n\n// Example usage:\n$string1 = "152.15 x 12.34 x 11mm";\n$string2 = "152.15x12.34x11 mm";\n$string3 = "152.15mmx12.34mm x 11mm";\n\n$dim1 = extractDimensions($string1);\n$dim2 = extractDimensions($string2);\n$dim3 = extractDimensions($string3);\n\nprint_r($dim1); // Output: Array ( [0] => 152.15 [1] => 12.34 [2] => 11 )\nprint_r($dim2); // Output: Array ( [0] => 152.15 [1] => 12.34 [2] => 11 )\nprint_r($dim3); // Output: Array ( [0] => 152.15 [1] => 12.34 [2] => 11 )