<?php\nsession_start();\n\n// Check if the test cookie is set\nif (!isset($_SESSION['cookie_test'])) {\n    // Set the session variable as a test cookie\n    $_SESSION['cookie_test'] = true;\n    \n    // Redirect to recheck in another request\n    header("Location: " . $_SERVER['PHP_SELF']);\n    exit();\n} else {\n    // Check if the session persists\n    if ($_SESSION['cookie_test']) {\n        echo "Cookies are enabled. You can use the site normally.";\n        unset($_SESSION['cookie_test']);  // Clean up after test\n    } else {\n        echo "<p>Cookies seem to be disabled in your browser.</p>";\n        echo "<p>Please enable cookies or refer to our help page for instructions on how to do this.</p>";\n        echo "<a href='screenshots.html'>How to enable cookies</a>";\n    }\n}\n?>