<?php\n// List of approved origins\n$approvedOrigins = ['https://approved-site1.com', 'https://approved-site2.com'];\n\n// Create the frame-ancestors directive for CSP\n$cspDirective = "frame-ancestors ";\nforeach ($approvedOrigins as $origin) {\n    $cspDirective .= "$origin ";\n}\n$cspDirective .= "'self';"; // Add this if you want to allow framing by your own pages\n\n// Set the Content-Security-Policy header\nheader("Content-Security-Policy: " . $cspDirective);\n\n?>