function transformArrayWithMapping($originalArray, $mapping) {\n    $transformedArray = [];\n    \n    foreach ($originalArray as $id => $count) {\n        if (isset($mapping[$id])) {\n            $humanReadableKey = $mapping[$id];\n            $transformedArray[$humanReadableKey] = $count;\n        } else {\n            // Handle cases where there is no mapping for the ID\n            // Option 1: Skip it\n            // Option 2: Keep the original ID as key or handle differently\n            $transformedArray[$id] = $count; \n        }\n    }\n    \n    return $transformedArray;\n}\n\n// Example usage:\n$originalArray = [\n    101 => 5,\n    203 => 8,\n    456 => 3\n];\n\n$mapping = [\n    101 => 'Apple',\n    203 => 'Banana',\n    456 => 'Cherry'\n];\n\n$resultArray = transformArrayWithMapping($originalArray, $mapping);\n\nprint_r($resultArray);