// Initialize cURL session\n$ch = curl_init();\n\n// First request to login and save cookie\ncurl_setopt($ch, CURLOPT_URL, "http://remoteServer/login.php?user=xxx&pass=yyy");\ncurl_setopt($ch, CURLOPT_COOKIEJAR, 'cookie.txt'); // Save cookies to a file\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n$output = curl_exec($ch);\n\n// Second request using the saved cookie\ncurl_setopt($ch, CURLOPT_URL, "http://remoteServer/pageicareabout.html");\ncurl_setopt($ch, CURLOPT_COOKIEFILE, 'cookie.txt'); // Load cookies from a file\n$output = curl_exec($ch);\n\n// Close cURL session\ncurl_close($ch);\n\necho $output;