// intval example\n$value = "abc123";\necho intval($value); // Outputs: 0\n\n// Type casting example\n$value = "abc123";\necho (int)$value; // Outputs: 0\n\n// More complex example with a string starting with a number\n$value = "123abc";\necho intval($value); // Outputs: 123\necho (int)$value; // Also outputs: 123\n\n// Handling null\n$value = null;\necho intval($value); // Outputs: 0\necho (int)$value; // Outputs: 0