<?php\n\nfunction fetchMultipleUrls($urls) {\n    $mh = curl_multi_init();\n    $chs = [];\n\n    // Initialize cURL sessions for each URL\n    foreach ($urls as $url) {\n        $ch = curl_init($url);\n        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n        curl_multi_add_handle($mh, $ch);\n        $chs[$url] = $ch;\n    }\n\n    $active = null;\n\n    // Execute the handles in parallel\n    do {\n        $status = curl_multi_exec($mh, $active);\n\n        if ($active) {\n            curl_multi_select($mh); // blocks until there's activity\n        }\n    } while ($active && $status === CURLM_OK);\n\n    // Retrieve and remove each handle from multi-handle session\n    foreach ($chs as $url => $ch) {\n        try {\n            $res = curl_multi_getcontent($ch);\n            echo "URL: $url\n";\n            echo "Response:\n$res\n----------\n";\n\n            // Check for errors here\n            if (curl_errno($ch)) {\n                throw new Exception('cURL error: ' . curl_error($ch));\n            }\n        } catch (Exception $e) {\n            echo "Error processing URL: $url - " . $e->getMessage() . "\n";\n        }\n\n        curl_multi_remove_handle($mh, $ch);\n    }\n\n    // Close handle\n    curl_multi_close($mh);\n}\n\n$urls = [\n    'http://example.com',\n    'http://another-example.com',\n    // Add more URLs here...\n];\n\nfetchMultipleUrls($urls);\n\n?>