$name = "Alice";\n$age = 30;\n$height = 5.6789;\n$money = 1234.56;\n\n// Example of %s and %d\necho sprintf("My name is %s and I am %d years old.\n", $name, $age);\n\n// Example of %.2f for formatting a float to two decimal places\necho sprintf("I am %.2f meters tall.\n", $height);\n\n// Example using %06.2f to pad with zeros up to 6 characters including 2 decimal places\necho sprintf("Money formatted: %06.2f\n", $money);\n\n// Example of hexadecimal output\n$number = 255;\necho sprintf("Hexadecimal representation of %d is %x.\n", $number, $number);\n\n// Using %% to print a literal percent sign\necho sprintf("This is 50%% of the total.\n");\n\n// Example using positional parameters\necho sprintf("%2$s says hello to %1$s.\n", "Bob", $name);