private function _processBotanyWords($match) {\n    // Check if the match is inside a title attribute using preg_match\n    if (preg_match('/\<[^>]*\btitle=".*?\bawesome\b.*?"[^>]*>/i', $this->context)) {\n        return $match[0]; // Return the original match if it's within a title attribute\n    }\n    \n    $botanyWords = $this->_botanyWords;  \n    $word = $match[0];\n    if (array_key_exists(strtolower($word), $botanyWords)) {    \n        return '<a href="' . PATH_BASE . 'articles/botany-words/#botany-word-' . str_replace(' ', '-', strtolower($word)) . '" title="' . trim($botanyWords[$word]) . '">' . $word . '</a>';\n    } else {\n        return $word;\n    }\n}